import React, { Component } from 'react'
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { Grid } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const styles = theme=> ({
    card:{
        margin: '10px',
        color: '#FFE81F',
        backgroundColor: 'black'
    },
    name:{

        borderBottom: '2px white'
    },
    root:{
        color: '#FFE81F'
    },
    table:{
        backgroundColor:'black',
        color: '#FFE81F'
    },
    loading:{
        textAlign:"center",
        color:'#FFE81F'
    }

});


export class Person extends Component {

    constructor(props) {
        super(props);
    }

    render() {

        let { classes } = this.props;

        return (
            <div>

                        <Paper className={classes.root}>
                            <Table className={classes.table} aria-label="simple table">

                                <TableHead>
                                    <TableRow>
                                        <TableCell>Name</TableCell>
                                        <TableCell align="right">Birth Year</TableCell>
                                        <TableCell align="right">Gender</TableCell>
                                        <TableCell align="right">Eye color</TableCell>
                                        <TableCell align="right">Skin Color</TableCell>
                                        <TableCell align="right">More Details</TableCell>
                                    </TableRow>
                                </TableHead>

                                <TableBody>
                                    {(() => {
                                        if(this.props.people == null){
                                            return (
                                                <h1 className={classes.loading}>Loading</h1>
                                            )
                                        }else{
                                            return(
                                                this.props.people.map(person => (
                                                <TableRow row={person.name}>
                                                    <TableCell component="th" scope="row">{person.name}</TableCell>
                                                    <TableCell align="right">{person.gender}</TableCell>
                                                    <TableCell align="right">{person.birth_year}</TableCell>
                                                    <TableCell align="right">{person.eye_color}</TableCell>
                                                    <TableCell align="right">{person.skin_color}</TableCell>
                                                    <TableCell align="right"><Button>Know More</Button></TableCell>
                                                </TableRow>
                                                ))
                                                )
                                                }
                                            })()}
                                </TableBody>

                            </Table>
                        </Paper>

            </div>
        )
    }
}

Person.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Person);
