import React, { Component } from 'react'
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { useQuery } from '@apollo/react-hooks';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import poster from './poster.jpg';
import Person from './Person';
import { Grid } from '@material-ui/core';
import { useEffect } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';

const styles = theme=> ({

    poster:{
        width: '100%',
        height: '300px'
    },
    home:{
        margin: '40px',
        paddingTop: '100px',
    },
    title:{

        padding: '15px',
        color: '#FFE81F',
        borderRadius: '10px',
        backgroundColor: 'black',
        textAlign: 'center'
    }
})

const client = new ApolloClient({
    uri: 'http://localhost:4000/graphql',
  });

const getPerson = (pageNo) =>{

    const all  = gql`
    query{

        getEveryPerson(page: ${pageNo}){
            count
            next
            results{
                name
                gender
                skin_color
                birth_year
                eye_color

            }
        }
    }
    `;

    return all;

}

export class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {

            currentPage: 1,
            people: null,
            count: null,

        }
    }

    makeQuery = async pageNo =>{
        await client.query({
                    query: getPerson(pageNo)
                                })
                                .then(result => {
                                    const data = result.data
                                    console.log('data:')
                                    this.setState({
                                        count: data.getEveryPerson.count,
                                        people: data.getEveryPerson.results
                                    })
                                    console.log(this.state.people)
                                });
    }

    async componentDidMount(){
        await this.makeQuery(1);
        console.log('DATA');
        console.log(this.state.people);   
    }


    render() {

        let { classes } = this.props;

        return (


            <div className={classes.home}>
            <ApolloProvider client={client}>
                <div className={classes.People}>

                    <h1 className={classes.title}>CHARACTERS</h1>
                    <Grid container>
                    <Person people={this.state.people} />
                    {/*
                        <Query query={getPerson(1)}>
                        {
                            ({loading,error,data}) =>{
                                if(loading) return <h2>Loading ....</h2>
                                if(error) console.log(error)
                                console.log(data.getEveryPerson.results)

                                return <Person people={data.getEveryPerson.results} />

                            }                            
                        }
                    </Query>*/}
                    </Grid>

                </div>
                </ApolloProvider>
            </div>
        )
    }
}

Home.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Home);
