import React, { Component } from 'react'
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { useQuery } from '@apollo/react-hooks';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import poster from './poster.jpg';
import Person from './Person';
import Film from './Film.js';
import Planet from './Planet';
import Vehicle from './Vehicle';
import StarShip from './StarShip';
import { Grid } from '@material-ui/core';
import { useEffect } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

const client = new ApolloClient({
    uri: 'http://localhost:4000/graphql',
  });

const getPerson = (pageNo) =>{

    const all  = gql`
    query{

        getEveryPerson(page: ${pageNo}){

            results{
                name
                gender
                skin_color
                birth_year
                eye_color

            }
        }


    }
    `;

    return all;

}

const getFilm = () =>{

    const all = gql`
    query{
        getEveryFilm{
            count
            results{
                episode_id
                title
                director
                release_date
                created
            }
        }
    }
    `;
    return all;
}

const getPlanet = (pageNo) =>{

    const all  = gql`
    query{

        getEveryPlanet(page: ${pageNo}){

            results{
                name
                population
                gravity
                climate
                created
            }
        }


    }
    `;

    return all;

}

const getVehicle = (pageNo) =>{

    const all  = gql`
    query{

        getEveryVehicle(page: ${pageNo}){

            results{
                cargo_capacity
                cost_in_credits
                created
                crew
                model
                name
                passengers
            }
        }


    }
    `;

    return all;

}

const getStarShip = (pageNo) =>{

    const all  = gql`
    query{

        getEveryStarShip(page: ${pageNo}){

            results{
                cargo_capacity
                cost_in_credits
                created
                crew
                model
                name
                passengers
                MGLT
                hyperdrive_rating
                manufacturer

            }
        }


    }
    `;

    return all;

}


export class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {          
            people: null,
            film: null,
            planet: null,
            Vehicle: null,
            StarShip: null,
            Species: null,       
        }
    }

    QueryPeople = async pageNo =>{
        await client.query({
                    query: getPerson(pageNo)
                        })
                        .then(result => {
                            const data = result.data   
                            this.setState({
                                people: data.getEveryPerson.results
                                })

                        });
    }

    QueryFilm = async () =>{
        await client.query({
                    query: getFilm()
                            })
                            .then(result => {
                                 const data = result.data
                                this.setState({
                                    film: data.getEveryFilm.results
                                })
                            });
    }

    QueryPlanet = async pageNo =>{
        await client.query({
                    query: getPlanet(pageNo)
                            })
                            .then(result => {
                                const data = result.data  
                                this.setState({
                                    planet: data.getEveryPlanet.results
                                })

                            });
    }

    QueryVehicle = async pageNo =>{
        await client.query({
                    query: getVehicle(pageNo)
                            })
                            .then(result => {
                                const data = result.data  
                                this.setState({
                                    Vehicle: data.getEveryVehicle.results
                                })

                            });
    }

    QueryStarShip = async pageNo =>{
        await client.query({
                    query: getStarShip(pageNo)
                            })
                            .then(result => {
                                const data = result.data  
                                this.setState({
                                    StarShip: data.getEveryStarShip.results
                                })

                            });
    }

    async setData(point,pageNo){
        if(point == 'person'){
            await this.QueryPeople(pageNo);
        }
        if(point == 'planet'){
            await this.QueryPlanet(pageNo);
        }
        if(point == 'vehicle'){
            await this.QueryVehicle(pageNo);
        }
        if(point == 'starship'){
            await this.QueryStarShip(pageNo);
        }


    }
    async componentDidMount(){
        await this.setData('person',1);
        await this.setData('planet',1);
        await this.setData('vehicle',1);
        await this.setData('starship',1);
        await this.QueryFilm();

    }

    render() {

        const number = [1,2,3,4,5,6,7,8,9];
        let { classes } = this.props;

        return (


            <div className={classes.home}>
            <ApolloProvider client={client}>
                <div className={classes.people}>

                    <h1 className={classes.title}>CHARACTERS</h1>

