const SW_PEOPLE  = gql`
    query{

        getEveryPerson(page: 1){
            count
            next
            results{
                name
                gender
                skin_color
                birth_year
                eye_color

            }
        }
    }
    `;

function MakeQuery({pageNo}) {
    const {loading, error, data} = useQuery(SW_PEOPLE,{
        variables: { pageNo }
    });

    return data.getEveryPerson;
}


function ChangeState(){
    const data = MakeQuery(1);
    console.log(data);
}


